/* Navigation toggler */
(function($) {
	$(document).ready(function() {

		$('.main-navigation-toggle').click(function (e) {
			$('#main-navigation').toggleClass('is-open');
			$('.main-navigation-toggle').toggleClass('is-open');
			e.preventDefault();
		});
		
	});
})(jQuery);

